DROP TABLE IF EXISTS `posts`;
/*!40101 SET @saved_cs_client     = @@character_set_client */;
/*!40101 SET character_set_client = utf8 */;
CREATE TABLE `posts` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `title` varchar(255) NOT NULL COMMENT 'Заголовок новости',
  `subtitle` varchar(255) DEFAULT NULL COMMENT 'Подзаголовок новости',
  `content` text NOT NULL COMMENT 'Содержимое новости',
  `category_id` int(11) NOT NULL COMMENT 'Внешний ключ для связи с таблицей разделов',
  `employee_id` int(11) DEFAULT NULL COMMENT 'Внешний ключ для связи с сотрудниками',
  `country_id` int(11) DEFAULT NULL COMMENT 'Внешний ключ для связи со странами',
  `is_published` tinyint(1) NOT NULL DEFAULT '0' COMMENT '1 - опубликовано (отображается), 0 - не опубликовано (не отображается)',
  `is_deleted` tinyint(1) NOT NULL DEFAULT '0' COMMENT '1 - удалено (фактически скрыто без возможности отображения), 0 - обычная запись',
  `is_comments_enabled` tinyint(1) NOT NULL DEFAULT '1' COMMENT '1 - допускается размещение пользовательских комментариев, 0 - не разрешено оставлять комментарии',
  `is_exclusive` tinyint(1) NOT NULL DEFAULT '0' COMMENT 'Эксклюзивный материал, изображения защищаются водяным знаком',
  `post_status_id` int(11) DEFAULT NULL COMMENT 'Внешний ключ, для связи с таблицей статусов',
  `is_infograph` tinyint(1) NOT NULL DEFAULT '0' COMMENT '1 - помечено как инфографика (водяной знако на главное изображение), 0 - обычная новость',
  `is_live_from_brasilia` tinyint(1) NOT NULL DEFAULT '0' COMMENT '1 - помечено как ''Live из Бразили'' (водяной знако на главное изображение), 0 - обычная новость',
  `is_top` tinyint(1) NOT NULL DEFAULT '0' COMMENT '1 - Новость выводится в TOP-новостях, 0 - не выводится',
  `is_breaknews` tinyint(1) DEFAULT '0' COMMENT 'Срочная новость',
  `delta` tinyint(1) NOT NULL DEFAULT '1' COMMENT 'Индекс для полнотекстового поиска (Sphinks)',
  `comments_count` int(11) NOT NULL DEFAULT '0' COMMENT 'Количество комментариев к новости',
  `published_at` datetime NOT NULL COMMENT 'Дата и время публикации',
  `created_at` datetime NOT NULL,
  `updated_at` datetime NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=289 DEFAULT CHARSET=utf8 COMMENT='Новости';
